mapper.insert:
    insert into BOSS.BOSS_ROLE_PERMISSION (PERMISSION_ID, ROLE_ID, "STATUS", 
      CREATE_TIME, UPDATE_TIME
      )
    values (
			#{permissionId,jdbcType=INTEGER}, 
			#{roleId,jdbcType=INTEGER}, 
      		#{status,jdbcType=INTEGER}, 
      		#{createTime,jdbcType=TIMESTAMP}, 
      		#{updateTime,jdbcType=TIMESTAMP}
      )

mapper.update:
       update boss.boss_role_permission set status=1,update_time=#{now} 
       where role_id=#{roleId} 
       and status=0
        and permission_id in (
        	 select permission_id from boss_permission 
       		start with permission_id=#{parentId} connect by  parent_id=prior permission_id
			)

update boss.boss_role_permission set
"STATUS" = #{status},
UPDATE_TIME = #{updateTime} 
WHERE role_id=#{roleId} 
and PERMISSION_ID=#{permissionId}
