mapper.insert:
insert into ZYC.BUSI_ORDER_LOG
ID, USERID, BUYER, ORDERID, WLID, LISTINGID, OPTYPE, CREATETIME, UPDATETIME, REMARKS, ORDER_SNAPSHOT,
ZYC.SEQ_BUSI_ORDER_LOG.nextval, #{userid,jdbcType=DECIMAL}, #{buyer,jdbcType=DECIMAL}, #{orderid,jdbcType=VARCHAR}, #{wlid,jdbcType=VARCHAR}, #{listingid,jdbcType=VARCHAR}, #{optype,jdbcType=DECIMAL}, #{createtime,jdbcType=TIMESTAMP}, #{updatetime,jdbcType=TIMESTAMP}, #{remarks,jdbcType=CLOB}, #{orderSnapshot,jdbcType=VARCHAR}

mapper.select:
	select 

  ,

  from ZYC.BUSI_ORDER_LOG
  where ORDERID = #{orderId}
  order by CREATETIME ${sortFlg}
