  values (#{qualityItemId,jdbcType=DECIMAL}, #{wlid,jdbcType=VARCHAR}, #{qualityInfoId,jdbcType=DECIMAL}, 
    #{qualityItemType,jdbcType=VARCHAR}, #{qualityItemName,jdbcType=VARCHAR}, #{qualityItemStandard,jdbcType=VARCHAR}, 
    #{qualityItemResult,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
    )
