mapper.select:
select o.QUOTE_ID, o.PURCHASE_ID, o.PURCHASE_CODE, o.QUOTE_PRICE, o.QUOTE_DESCRIPTION, o.QUOTER, o.PHONE, o."STATUS", o.IS_VALID, o.CREATE_TIME, o."OPERATOR", o.UPDATE_TIME, b.USER_NAME, b.MOBILE, p.BREED_NAME
from ZYC.BUSI_QUOTE o left join ZYC.BUSI_PURCHASE p ON o.PURCHASE_ID=p.PURCHASE_ID left join BOSS.UC_USER u on (p.PURCHASER = u.user_name and u.STATUS = 0) left join BOSS.BOSS_USER b on u.SALESMANID=b.USER_ID
where 1 = 1
and o.QUOTER = #{params.quoter}
and p.BREED_NAME = #{params.breedname}
and o."STATUS" = #{params.status}
and o."STATUS" = 0
and o.CREATE_TIME > to_date(#{params.StartDate},'yyyy-mm-dd hh24:mi:ss')
and o.CREATE_TIME < to_date(#{params.EndDate},'yyyy-mm-dd hh24:mi:ss') + 1
order by o.CREATE_TIME desc

mapper.insert:
insert into ZYC.BUSI_QUOTE (QUOTE_ID, PURCHASE_ID, PURCHASE_CODE, 
  QUOTE_PRICE, QUOTE_DESCRIPTION, QUOTER, 
  PHONE, "STATUS", IS_VALID, 
  CREATE_TIME, "OPERATOR", UPDATE_TIME
  )
values (ZYC.SEQ_BUSI_QUOTE.nextval, #{purchaseId,jdbcType=VARCHAR}, #{purchaseCode,jdbcType=VARCHAR}, 
  #{quotePrice,jdbcType=DECIMAL}, #{quoteDescription,jdbcType=VARCHAR}, #{quoter,jdbcType=VARCHAR}, 
  #{phone,jdbcType=VARCHAR}, #{status,jdbcType=DECIMAL}, #{isValid,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{operator,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.update:
	update ZYC.BUSI_QUOTE
  set
    "STATUS" = #{status,jdbcType=DECIMAL},
    "OPERATOR" = #{operator,jdbcType=VARCHAR},
    UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}
  where
    PURCHASE_ID = #{purchaseId,jdbcType=VARCHAR}
    and QUOTE_ID <> #{quoteId,jdbcType=VARCHAR}
    and IS_VALID = 'Y'
