mapper.select:
       select CODE,NAME,TYPE,FIRSTCODE from ZYC.AREA
       
       	
	    	CODE = #{code,jdbcType=VARCHAR}
	    
	    
	    	and NAME = #{name,jdbcType=VARCHAR}
	    
	    
	    	and TYPE = #{type,jdbcType=VARCHAR}
	    
	    
	    	and FIRSTCODE = #{firstcode,jdbcType=VARCHAR}
