  `area_id` int(10) NOT NULL COMMENT '企业注册地省份',
  `contact_name` varchar(130) NOT NULL COMMENT '联系人姓名',
  `contact_mobile` varchar(11) NOT NULL COMMENT '联系人手机',
  `online_status` bit(1) NOT NULL DEFAULT b'1' COMMENT '在线状态（0：离线，1：在线）',
  `bind_erp` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否绑定erp（0：未绑定，1：绑定）',
