mapper.select:
select
enquiry_bills.id as id,code,user_id,member_id,status,enquiry_bills.create_time as create_time,
enquiry_bills.update_time as update_time,update_user,quoted_time,
user_name AS userName,company_full_name as companyFullName,area_full as areaFull,
user.contact_mobile as contactMobile, user.contact_name as contactName
from enquiry_bills
left join user on user_id = user.id


        enquiry_bills.id = #{id}



        and code like "%"#{code}"%"



        and user_id = #{userId}



        and member_id = #{memberId}



        and status = #{status}



        and enquiry_bills.create_time = #{createTime}


        and enquiry_bills.create_time  >=  #{startTime}



        and enquiry_bills.create_time  <=  #{endTime}



        and user_name like "%"#{userName}"%"



        and company_full_name like "%"#{companyFullName}"%"



        and area_full like "%"#{areaFull}"%"


order by enquiry_bills.id DESC
